void ExibirOpcoesDoMenu()
{
    Console.WriteLine("Digite 1 para registrar uma banda");
    Console.WriteLine("Digite 2 para mostrar todas as bandas");
    Console.WriteLine("Digite 3 para avaliar uma banda");
    Console.WriteLine("Digite 4 para exibir a média de uma banda");
    Console.WriteLine("Digite 9 para sair");
    
    Console.WriteLine("Digite a sua opção: ");
    var opcaoEscolhida = Console.ReadLine()!;
    var opcaoEscolhidaInt = int.Parse(opcaoEscolhida);

    switch (opcaoEscolhidaInt)
    {
        case 1 : RegistrarBanda();
            break;
        case 2 : MostrarBandasRegistradas();
            break;
        case 3 :
            AvaliarUmaBanda();
            break;
        case 4 :
            ExibirMediaBanda();
            break;
        case 9 : Console.WriteLine("Fechando o programa.");
            break;
        default: Console.WriteLine("Opção Invalida!");
            break;
    }
}

void RegistrarBanda()
{
    Console.Clear();
    ExibirTituloDaOpcao("Registro das bandas");
    Console.WriteLine("Digite uma banda: ");
    var nomeBanda = Console.ReadLine()!;
    bandasRegistradas.Add(nomeBanda, new List<int>());
    Console.WriteLine($"A Banda {nomeBanda} foi registrada com sucesso!");
    Thread.Sleep(2000);
    Console.Clear();
    ExibirOpcoesDoMenu();
}

void MostrarBandasRegistradas()
{
    Console.Clear();
    ExibirTituloDaOpcao("Exibindo todas as bandas registradas");
    
    foreach (string banda in bandasRegistradas.Keys)
    {
        Console.WriteLine($"Banda: {banda}");
    }
    

    Console.WriteLine("Digite qualquer tecla para voltar ao menu principal ");
    Console.ReadKey();
    Console.Clear();
    ExibirOpcoesDoMenu();
}

void ExibirTituloDaOpcao(String titulo)
{
    int quantidadeDeLetras = titulo.Length;
    string asteriscos = string.Empty.PadLeft(quantidadeDeLetras, '*');
    Console.WriteLine(asteriscos);
    Console.WriteLine(titulo);
    Console.WriteLine(asteriscos + "\n");
}

void AvaliarUmaBanda()
{
    Console.Clear();
    ExibirTituloDaOpcao("Avaliar Banda");
    Console.Write("Digite o nome da banda que deseja avaliar: ");
    string nomeDaBanda = Console.ReadLine()!;

    if (bandasRegistradas.ContainsKey(nomeDaBanda))
    {
        Console.WriteLine($"Qual a nota que a banda {nomeDaBanda} merece: ");
        int nota = Convert.ToInt32(Console.ReadLine());
        bandasRegistradas[nomeDaBanda].Add(nota);
        Console.WriteLine($"\n A nota foi registrada com sucesso!");
        Thread.Sleep(1500);
        Console.Clear();
        ExibirOpcoesDoMenu();
    }
    else
    {
        Console.WriteLine($"A Banda {nomeDaBanda} não foi  encontrada!");
        Console.WriteLine("Digite qualquer tecla para voltar pro menu principal");
        Console.ReadKey();
        Console.Clear();
        ExibirOpcoesDoMenu();
    }
}

void ExibirMediaBanda()
{
    Console.Clear();
    ExibirTituloDaOpcao("Exibir Media de Banda");
    Console.WriteLine("Digite a banda que deseja ver a media: ");
    var bandaMedia = Console.ReadLine()!;

    if (bandasRegistradas.ContainsKey(bandaMedia))
    {
        double soma = 0;
        for(int i = 0; i < bandasRegistradas[bandaMedia].Count ; i++){
            soma += bandasRegistradas[bandaMedia][i];
        }
        double media = soma / bandasRegistradas[bandaMedia].Count();
        Console.WriteLine($"A Media da banda {bandaMedia} é: {media}");
    }
    else
    {
        Console.WriteLine("Essa banda não está registrada!");
    }
    Console.WriteLine("\nDigite alguam tecla para voltar ao menu principal.");
    Console.ReadKey();
    Console.Clear();
    ExibirOpcoesDoMenu();
}

